INSERT INTO pizzas (name, description, image_url, price) VALUES('Margherita', 'Pomodoro San Marzano DOP, mozzarella fior di latte, basilico fresco', 'https://www.cibotoday.it/~media/horizontal-hi/31583586473285/pizza-margherita-di-sofia.jpg', 8.00),('Marinara', 'Pomodoro San Marzano DOP, aglio, origano, olio extra vergine d''oliva', 'https://media-assets.lacucinaitaliana.it/photos/63c01847f5d7419a2dc115ef/1:1/w_1334,h_1334,c_limit/Pizza%20Marinara.jpg', 6.50),('Prosciutto e Funghi', 'Pomodoro, mozzarella, prosciutto cotto, funghi champignon', 'https://i1.wp.com/www.piccolericette.net/piccolericette/wp-content/uploads/2019/10/4102_Pizza.jpg?resize=895%2C616&ssl=1', 9.50),('Diavola', 'Pomodoro, mozzarella, salame piccante, peperoncino', 'https://pizzeriadoifanti.it/wp-content/uploads/2020/04/diavola.jpg', 9.00),('Quattro Formaggi', 'Pomodoro, mozzarella, gorgonzola DOP, Parmigiano Reggiano DOP, fontina DOP', 'https://au.gozney.com/cdn/shop/files/Four_Cheese_Pizza_Feng_Chen_-_Large_783797e3-5895-4737-bf88-6b5c6f1013bb.png?v=1743106161&width=1500', 10.50),('Capricciosa', 'Pomodoro, mozzarella, prosciutto cotto, funghi, carciofi, olive nere', 'https://www.italianstylecooking.net/wp-content/uploads/2022/01/Pizza-capricciosa.jpg', 10.00),('Vegetariana', 'Pomodoro, mozzarella, verdure miste di stagione (peperoni, melanzane, zucchine)', 'https://fiordigrano.minutocaffe.it/wp-content/uploads/2021/01/05444.jpg', 8.50),('Napoletana', 'Pomodoro San Marzano DOP, mozzarella fior di latte, acciughe, capperi, origano', 'https://www.saporinostri.it/wp-content/uploads/2020/10/le-origini-della-pizza-napoletana-e1601990439881-1024x604.jpg', 9.00),('Salsiccia e Friarielli', 'Mozzarella, salsiccia fresca, friarielli (cime di rapa saltate)', 'https://www.scattidigusto.it/wp-content/uploads/2025/01/pizza-salsiccia-e-friarielli.jpg', 10.00),('Bianca', 'Mozzarella fior di latte, ricotta, spinaci, olio extra vergine d''oliva (senza pomodoro)', 'https://scarlatifamilykitchen.com/wp-content/uploads/2024/02/white-pizza-bianca-1200x1200-1.jpg', 9.50);